void init_snake(Snake *snake) {
    snake->head.x = 320;
    snake->head.y = 240;
    snake->tail.x = snake->head.x - 10;
    snake->tail.y = snake->head.y;
    snake->length = 2;
    snake->direction = 0;  // 0: up, 1: right, 2: down, 3: left
}

void move_snake(Snake *snake) {
    int dx, dy;

    switch (snake->direction) {
        case 0:  // Up
            dy = -10;
            dx = 0;
            break;
        case 1:  // Right
            dy = 0;
            dx = 10;
            break;
        case 2:  // Down
            dy = 10;
            dx = 0;
            break;
        case 3:  // Left
            dy = 0;
            dx = -10;
            break;
    }

    snake->head.x += dx;
    snake->head.y += dy;

    if (snake->length > 2) {
        for (int i = snake->length - 1; i > 0; i--) {
            snake->tail.x = snake->head.x - (i - 1) * 10;
            snake->tail.y = snake->head.y;
        }
    }
}

void draw_snake(int screen_width, int screen_height, const Snake *snake, SDL_Texture *texture) {
    int x = snake->head.x;
    int y = snake->head.y;

    SDL_Rect rect = { x, y, 10, 10 };
    SDL_RenderCopy(renderer, texture, NULL, &rect);
}

void handle_input(Snake *snake) {
    SDL_Event e;
    while (SDL_PollEvent(&e) != 0) {
        if (e.type == SDL_KEYDOWN) {
            switch (e.key.keysym.sym) {
                case SDLK_UP:
                    if (snake->direction != 2) snake->direction = 0;
                    break;
                case SDLK_RIGHT:
                    if (snake->direction != 3) snake->direction = 1;
                    break;
                case SDLK_DOWN:
                    if (snake->direction != 0) snake->direction = 2;
                    break;
                case SDLK_LEFT:
                    if (snake->direction != 1) snake->direction = 3;
                    break;
            }
        }
    }
}
