```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

// Function to count the occurrences of a word in a string
int countWordOccurrences(const std::string& line, const std::string& word) {
    std::istringstream stream(line);
    std::string token;
    int count = 0;
    while (std::getline(stream, token, ' ')) { // Split by spaces
        if (token == word) {
            count++;
        }
    }
    return count;
}

int main() {
    std::string filename, word;
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to search: ";
    std::cin >> word;

    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return 1;
    }

    std::string line;
    int totalOccurrences = 0;
    while (std::getline(file, line)) {
        totalOccurrences += countWordOccurrences(line, word);
    }

    file.close();

    std::cout << "The word \"" << word << "\" appears " << totalOccurrences << " times in the file." << std::endl;

    return 0;
}
