```c
#include <allegro5/allegro.h>
#include <allegro5/allegro_image.h>
#include <allegro5/allegro_primitives.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>

#define SNAKE_SIZE 16
#define SNAKE_COLOR ALLEGRO_COLOR{1, 1, 0, 1}
#define FOOD_COLOR ALLEGRO_COLOR{1, 0, 0, 1}

ALLEGRO_DISPLAY *display = NULL;
ALLEGRO_EVENT_QUEUE *event_queue = NULL;
ALLEGRO_TIMER *timer = NULL;

ALLEGRO_BITMAP *snake_img = NULL;
ALLEGRO_BITMAP *food_img = NULL;

ALLEGRO_COLOR snake_color = SNAKE_COLOR;
ALLEGRO_COLOR food_color = FOOD_COLOR;

typedef struct {
    int x, y;
} Segment;

typedef struct {
    Segment head;
    int length;
} Snake;

Snake snake = {{200, 200}, 1};

int direction = 0; // 0: up, 1: right, 2: down, 3: left
bool gameOver = false;

// Function to draw a segment
void draw_segment(Segment segment) {
    al_draw_bitmap(snake_img, segment.x, segment.y, 0);
}

// Function to draw the snake
void draw_snake() {
    for (int i = 0; i < snake.length; i++) {
        draw_segment(snake.head);
        snake.head.x += (i % 2) ? -SNAKE_SIZE : 0;
        snake.head.y += (i % 2) ? 0 : -SNAKE_SIZE;
    }
}

// Function to handle input
void handle_input() {
    ALLEGRO_EVENT ev;
    al_wait_for_event(event_queue, &ev);
    if (ev.type == ALLEGRO_EVENT_KEY_DOWN) {
        switch (ev.keyboard.keycode) {
            case ALLEGRO_KEY_UP:
                if (direction != 2) direction = 0;
                break;
            case ALLEGRO_KEY_DOWN:
                if (direction != 0) direction = 2;
                break;
            case ALLEGRO_KEY_LEFT:
                if (direction != 1) direction = 3;
                break;
            case ALLEGRO_KEY_RIGHT:
                if (direction != 3) direction = 1;
                break;
            case ALLEGRO_KEY_ESCAPE:
                gameOver = true;
                break;
        }
    }
}

int main() {
    if (!al_init()) {
        fprintf(stderr, "Failed to initialize Allegro\n");
        return -1;
    }

    if (!al_init_image_addon()) {
        fprintf(stderr, "Failed to initialize image addon\n");
        return -1;
    }

    display = al_create_display(800, 600);
    if (!display) {
        fprintf(stderr, "Failed to create display\n");
        return -1;
    }

    event_queue = al_create_event_queue();
    if (!event_queue) {
        fprintf(stderr, "Failed to create event queue\n");
        return -1;
    }

    al_install_keyboard();
    al_register_event_source(event_queue, al_get_keyboard_event_source());
    al_register_event_source(event_queue, al_get_display_event_source(display));

    timer = al_create_timer(0.15); // Adjust frame rate
    if (!timer) {
        fprintf(stderr, "Failed to create timer\n");
        return -1;
    }
    al_start_timer(timer);

    al_init_primitives_addon();

    // Load images
    snake_img = al_load_bitmap("snake.png");
    food_img = al_load_bitmap("food.png");
    if (!snake_img || !food_img) {
        fprintf(stderr, "Failed to load images\n");
        return -1;
    }

    al_clear_to_color(al_map_rgb(0, 0, 0));
    al_flip_display();

    while (!gameOver) {
        handle_input();
        al_wait_for_event(event_queue, &ev);

        if (ev.type == ALLEGRO_EVENT_TIMER) {
            // Update game logic
            snake.head.x += (direction % 2) ? 16 : 0;
            snake.head.y += (direction / 2) ? 16 : 0;

            // Check collision with walls
            if (snake.head.x < 0 || snake.head.x >= 800 || snake.head.y < 0 || snake.head.y >= 600) {
                gameOver = true;
            }

            // Check for food
            if (snake.head.x == 320 && snake.head.y == 240) {
                // Grow snake
                snake.length++;
            }

            // Redraw
            al_clear_to_color(al_map_rgb(0, 0, 0));
            al_draw_bitmap(food_img, 320, 240, 0);
            draw_snake();
            al_flip_display();
        }
    }

    al_destroy_bitmap(snake_img);
    al_destroy_bitmap(food_img);
    al_destroy_event_queue(event_queue);
    al_destroy_timer(timer);
    al_destroy_display(display);

    return 0;
}
```

### Key Points
1. **Efficiency**: The frame rate is controlled by the timer, and image drawing is optimized.
2. **User Input**: Handles arrow keys for movement.
3. **Game Logic**: Updates snake position, handles collisions, and food consumption.
4. **Image Loading**: Uses Allegro 5 for loading and drawing images.

Ensure you have the correct image paths and tweak the timer as needed for desired performance. This example should provide a solid foundation for creating a smooth, image-based Snake game in C using Allegro 5.

