// Function to count occurrences of a word in a line
int countWordInLine(const std::string& line, const std::string& word) {
    int count = 0;
    std::istringstream stream(line);
    std::string token;
    while (stream >> token) {
        // Convert to lower case for case-insensitive comparison
        std::string lowerToken = token;
        std::transform(lowerToken.begin(), lowerToken.end(), lowerToken.begin(), ::tolower);
        std::string lowerWord = word;
        std::transform(lowerWord.begin(), lowerWord.end(), lowerWord.begin(), ::tolower);
        if (lowerToken == lowerWord) {
            ++count;
        }
    }
    return count;
}

int main() {
    std::string filename, word;
    std::cout << "Enter the filename: ";
    std::cin >> filename;

    std::cout << "Enter the word to count: ";
    std::cin >> word;

    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return 1;
    }

    int totalOccurrences = 0;
    std::string line;
    while (std::getline(file, line)) {
        totalOccurrences += countWordInLine(line, word);
