  console.log(`Logged in as ${client.user.tag}!`);
});

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  const { commandName } = interaction;

  switch (commandName) {
    case 'ban':
      await interaction.deferReply(); // Acknowledge command reception

      const member = interaction.guild.members.cache.get(interaction.options.getUser('user').id);
      const reason = interaction.options.getString('reason') || 'No reason provided';

      if (!interaction.member.permissions.has(Permissions.FLAGS.BAN_MEMBERS)) {
        return interaction.editReply({ content: 'Insufficient permissions to ban users.', ephemeral: true });
      }

      try {
        await member.ban({ reason });
        interaction.editReply({
          embeds: [new EmbedBuilder()
            .setColor(0x0099ff)
            .setTitle('User Banned')
            .setDescription(`User ${member.user.tag} has been banned.\nReason: ${reason}`)
          ]
        });
      } catch (error) {
        console.error(`Failed to ban user: ${error}`);
        interaction.editReply({ content: 'An error occurred while banning the user.', ephemeral: true });
      }
      break;

    default:
      console.warn(`Unhandled command: ${commandName}`);
  }
});

client.login('YOUR_BOT_TOKEN');
