// Simplified Markdown to HTML conversion (basic support)
std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::unordered_map<std::string, std::string> replacements = {
        {"\\*", "<em>"},
        {"\\*/", "</em>"},
        {"\\#", "<strong>"},
        {"\\#/", "</strong>"},
        {"\\-", "<del>"},
        {"\\\/-", "</del>"}
    };

    for (const auto& [key, value] : replacements) {
        html = markdown; // Start with the original markdown
        while (true) {
            size_t pos = html.find(key);
            if (pos == std::string::npos) break;
            html.replace(pos, key.length(), value);
        }
    }

    // Basic heading conversion (H1-H6)
    html.replace(html.find("# "), 2, "<h1>");
    html.replace(html.find("## "), 3, "<h2>");
    html.replace(html.find("### "), 4, "<h3>");
    html.replace(html.find("#### "), 5, "<h4>");
    html.replace(html.find("##### "), 6, "<h5>");
    html.replace(html.find("###### "), 7, "<h6>");

    // Basic paragraph and line break handling
    html.replace(html.find("\n\n"), 2, "</p><p>"); // Assume paragraphs are separated by double newlines

    return html;
}

