        // Handle events (key presses, closes)
        handleEvents(&state);

        // Cap frame rate (important for smoothness)
        float deltaTime = (float)(clock() - lastUpdate) / CLOCKS_PER_SEC;
        lastUpdate = clock();
        if (deltaTime < 0.05) { // Aim for 20 FPS minimum
            updateGame(&state);
            renderGame(&state, renderer);
        }

        // Limit to 60 FPS max (adjust as needed)
        if (deltaTime > 1 / 60.0) SDL_Delay((Uint32)((1 / 60.0) - deltaTime * 1000));
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
