    GatewayIntentBits.Guilds,
    GatewayIntentBits.GuildMessages,
    GatewayIntentBits.GuildMembers,
    GatewayIntentBits.GuildMessagesReactions,
  ],
  token: config.DISCORD_TOKEN,
});

client.once('ready', () => {
  console.log(`Bot is online!`);
});

client.on('guildMemberAdd', (member) => {
  if (!config.BANNED MEMBERS.includes(member.id)) {
    member.guild.members.cache.forEach((m) => {
      if (m.user.id === member.id) {
        member.ban({ reason: 'Temporary ban for breaching community rules' });
        member.remove();
      }
    });
  }
});

client.on('guildMemberRemove', (member) => {
  if (config.BANNED MEMBERS.includes(member.id)) {
    member.ban({ reason: 'Permanent ban for repeated infractions' });
  }
});

client.on('messageCreate', async (message) => {
  if (message.content.startsWith('/') && message.content.endsWith(':ban')) {
    const interaction = await message.interaction;
    const user = interaction.user;

    if (user.id === message.author.id) return;

    const reason = interaction.options.values()[0];
    const targetId = user.id;
    const targetGuild = message.guild;

    try {
      const targetMember = targetGuild.members.cache.get(targetId);
      if (!targetMember) return;

