// Function to count word occurrences in a file
int countWordOccurrences(const std::string& filename, const std::string& word) {
    int count = 0;
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return -1;
    }

    std::string line;
    while (getline(file, line)) {
        // Convert line to lowercase for case-insensitive comparison
        std::transform(line.begin(), line.end(), line.begin(), ::tolower);
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            count++;
            pos += word.length(); // Move past the found word to avoid overlapping matches
        }
    }
    file.close();
    return count;
}

int main() {
    std::string filename = "example.txt";
    std::string targetWord = "example";

    int occurrences = countWordOccurrences(filename, targetWord);

    if (occurrences >= 0) {
        std::cout << "The word \"" << targetWord << "\" appears " << occurrences 
                  << " times in " << filename << "." << std::endl;
    }

    return 0;
}
```

