void handle_input() {
    ALLEGRO_EVENT ev;
    while (al_get_next_event(event_queue, &ev)) {
        switch (ev.type) {
            case ALLEGRO_EVENT_DISPLAY_CLOSE:
                running = false;
                break;
            case ALLEGRO_EVENT_KEY_DOWN:
                switch (ev.keyboard.keycode) {
                    case ALLEGRO_KEY_UP:
                        if (dy == 0) {
