       const member = interaction.guild.members.resolve(user);

       if (!member) {
         return interaction.reply('This user is not in the server.');
       }

       try {
         await member.ban();
         interaction.reply(`${user.tag} has been banned.`);
       } catch (err) {
         console.error(err);
         interaction.reply('An error occurred while trying to ban the user.');
       }
     }
   });

   client.login(token);
   ```

