#include <string>
#include <regex>

// Function to convert markdown to HTML
std::string markdownToHTML(const std::string& markdown) {
    // Create a regex pattern for basic markdown to HTML conversion
    std::regex markdownPattern(R"(
    \* (.*) \*               # Bold: **Markdown** -> <strong>Markdown</strong>
    \_ (.*) \_               # Italic: _Markdown_ -> <em>Markdown</em>
    \# (.*)                  # Header: #Header -> <h1>Header</h1>
    \# (.*)                  # Sub-header: ##Header -> <h2>Header</h2>
    \# (.*)                  # Sub-sub-header: ###Header -> <h3>Header</h3>
    \[ (.*) \]\((.*)\)       # Hyperlink: [LinkText](URL) -> <a href="URL">LinkText</a>
    \!\[ (.*) \]\((.*)\)     # Image:![AltText](URL) -> <img src="URL" alt="AltText">
    )", std::regex_constants::dotall);

    // Use regex_replace to substitute markdown patterns with HTML tags
    std::string html = std::regex_replace(markdown, markdownPattern, [](const std::smatch& m) {
        std::string html;
        if (m[1].matched) {
            if (m[1][0] == '*') html = "<strong>" + m[1].str() + "</strong>";
            else if (m[1][0] == '_') html = "<em>" + m[1].str() + "</em>";
            else if (m[1][0] == '#') html = "<h1>" + m[1].str() + "</h1>";
            else if (m[1][0] == '[') html = "<a href=\"" + m[2].str() + "\">" + m[1].str() + "</a>";
            else if (m[1][0] == '!') html = "<img src=\"" + m[2].str() + "\" alt=\"" + m[1].str() + "\">";
        }
        return html;
    });

    // Replace multiple spaces with a single space
    html = std::regex_replace(html, std::regex(R"(\s+)"), " ");

    return html;
}

int main() {
    // Example markdown string
    std::string markdown = "# Welcome to Markdown! \n"
                           "This is a [link](https://example.com) and an![image](https://example.com/image.png). \n"
                           "Here is a *bold* text and an _italic_ text. \n"
                           "## This is a sub-header \n"
                           "***This is a sub-sub-header***";

    // Convert markdown to HTML
    std::string html = markdownToHTML(markdown);

    // Output the resulting HTML
    std::cout << html << std::endl;

    return 0;
}
```

