// Helper function to convert string to lowercase
std::string toLowerCase(const std::string& str) {
    std::string lowerStr = str;
    std::transform(lowerStr.begin(), lowerStr.end(), lowerStr.begin(), ::tolower);
    return lowerStr;
}

int main() {
    std::string fileName, wordToCount, line;
    int count = 0;

    // Prompt user for input
    std::cout << "Enter the name of the text file: ";
    std::cin >> fileName;
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    // Convert the word to lowercase
    std::string lowerWordToCount = toLowerCase(wordToCount);

    // Open the file
    std::ifstream file(fileName);
    if (!file) {
        std::cerr << "Error opening file." << std::endl;
        return 1;
    }

    // Read the file line by line
    while (std::getline(file, line)) {
        // Convert the line to lowercase
        std::string lowerLine = toLowerCase(line);

        // Split the line into words
        std::stringstream ss(lowerLine);
        std::string word;
        while (ss >> word) {
            // Check if the word is the same as the word to count
            if (word == lowerWordToCount) {
                ++count;
            }
        }
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word \"" << wordToCount << "\" appears " << count << " times in the file." << std::endl;

    return 0;
}
```

