const { Client, GatewayIntentBits, REST, Routes, SlashCommandBuilder, PermissionsBitField } = require('discord.js');
require('dotenv').config(); // for environment variables (optional, recommended)

const token = process.env.TOKEN; // replace with your bot token
const clientId = process.env.CLIENT_ID; // replace with your bot client ID
const guildId = process.env.GUILD_ID; // replace with your guild ID

const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildBans, GatewayIntentBits.GuildMembers] });

// Register the /ban command
const commands = [
    new SlashCommandBuilder()
       .setName('ban')
       .setDescription('Ban a user from the server.')
       .addUserOption(option =>
            option.setName('target')
               .setDescription('The user to ban')
               .setRequired(true))
       .addStringOption(option =>
            option.setName('reason')
               .setDescription('The reason for the ban')
               .setRequired(false))
].map(command => command.toJSON());

