// Helper function to check if a number is prime
function isPrime(num) {
    // Check if number is less than 2, as 0 and 1 are not prime numbers
    if (num < 2) {
        return false;
    }
    // Check for factors from 2 to the square root of the number
    for (let i = 2, sqrt = Math.sqrt(num); i <= sqrt; i++) {
        if (num % i === 0) {
            return false;
        }
    }
    return true;
}

// Main function to check for prime numbers in the array and print them
function checkPrimes(arr) {
    if (!Array.isArray(arr) || arr.length!== 5) {
        console.error("Input must be an array of exactly 5 numbers.");
        return;
    }

    for (let i = 0; i < arr.length; i++) {
        if (isPrime(arr[i])) {
            console.log(`Prime number found: ${arr[i]}`);
        }
    }
}

// Example usage
