from functools import wraps

def derivative(f, x):
    def inner(a, b):
        return (f(b) - f(a)) / (b - a)

    @wraps(f)
    def outer(x):
        return inner(f(x - 1), f(x))

    outer.__name__ = f.__name__
    return outer


@derivative
def user_defined_function(x):
    # Replace this function with the function you want to find the derivative of
    return x ** 2


def main():
