        testDescription: testNameParts[1],
        filePath: file,
        outcome: results.state,
      };
    });
    this.testResults.push(...screenshots);
  }

  write() {
    fs.writeFileSync('test-results.json', JSON.stringify(this.testResults, null, 2));
  }
