Firstly, install required libraries:
- For building, you'll need `boost-spirit-v3-devel` (Boost Spirit X3), `fmt` and a C++17 compliant compiler.
- You'll also need to clone the GitHub repository of enquirer/CmarkSharp (a C++ implementation of CommonMark/Markdown spec) and replace the provided implementation below with its parsing part.

Here's the implementation:
```cpp
#include <string>
#include <stdexcept>
#include <sstream>
#include <iomanip>
#include <boost/spirit/home/x3.hpp>
#include "cmark/cmark.h"

namespace markdown_to_html {
using namespace std;
using namespace boost::spirit::x3;

struct html_escape {
    std::string& str;
    auto& f = *this;

    template<typename T>
    void operator()(char c) const {
        if ((c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z') ||
            (c >= '0' && c <= '9') || c == '.' || c == ',' || c == '!')
            str += c;
        else {
            ostringstream oss;
            oss << "&#"x << setw(4) << setfill(static_cast<unsigned int>('0')) << hex << static_cast<int>(c);
            str += oss.str();
        }
    }

    html_escape(std::string& s) : str(s) {}
};

std::string markdownToHtml(const std::string& input) {
    std::string html;
    char output[CMARK_OPTION_DEFAULT_SOFT_BREAKS | CMARK_OPTION_ENABLE_TABLES];

    std::unique_ptr<cmark_parser, void (*)(cmark_parser*)> parser(cmark_parser_new(), cmark_parser_free);
    if (!cmark_parse(&output, strlen(CMARK_TRANSFORM_HTML), input.c_str(), input.size(), parser.get())) {
        throw runtime_error("Markdown parsing failed");
    }

    std::string escaped;
    html_escape esc(escaped);

    auto html_start = *"" << "<html><head><meta charset='utf-8'><title>Converted MD</title></head><body>";
    auto html_end = *</body></html>";

    bool in_code_block = false;
    bool in_link = false;
    string current_link_text;
    string current_link_href;

    auto html_rule = (
        *((
            ('<' >> -(html_escape, !alnum >> *space >> ';')) |
            ('&' >> (
                ('&') | ('amp;'[html_escape]) | ('quot;'[html_escape]) | ('apos;'[html_escape]) |
                ("lt;"[html_escape]) | ("gt;"[html_escape])
            ))
            )
        >> space |
            ('[' >> +(graph - ']([' >> -(escapespace >> ']')) ']')[
                in_link = true,
                current_link_text = _1,
                !(peek("]("))
            ] |
            (!in_code_block >> ("```"[this_blanks >> _] >> +(graph - "```"[this_blanks]))[
                in_code_block = true;
                esc |= html_escape
            ] |
            (!in_link && !in_code_block >> (+graph)[esc])
        )%
        (
            ((in_link) [
                if (!current_link_href.empty()) {
                    html += "<a href=\"" + current_link_href + "\">" + current_link_text + "</a>";
                    current_link_href.clear();
                    current_link_text.clear();
                    in_link = false;
                }
            ] |
            ((in_code_block) [esc = [](auto& c){}; in_code_block = false])
        ) |
            (("\n" >> -(space >> '\n'))[
                html += "<br>";
                esc = html_escape{escaped};
            ] |
            ('\n'[html += "<p>" + escaped + "</p>\n"; escaped.clear()])
        )
    );

    string md_input(input);

    auto r = phrase_parse(
        begin(md_input), end(md_input),
        html_rule,
        space,
        ascii_space);

    if (!r) throw runtime_error("Markdown parsing failed");

    html.insert(html.begin(), html_start);
    html += "\n";
    html += escaped.str();
    html += "\n";
    html.insert(html.end(), html_end);

    if (!current_link_href.empty()) {
        html.replace(html.find("<body>"), html.find("<body>") + 6, "<body>" +
            "<a href=\"" + current_link_href + "\">" + current_link_text + "</a>");
    }

    return html;
}
}

int main() {
    std::string md = "# Hello World\nThis is a [link](https://example.com). \n```\nCode block\n```";
    std::string html = markdown_to_html(md);
    cout << "Markdown:\n" << md << '\n';
    cout << "Converted HTML:\n" << html << '\n';
    return 0;
}
```
