std::string convert_markdown_to_html(const std::string& markdown) {
    // Map of markdown tags to HTML tags
    static const std::map<std::string, std::string> markdown_to_html = {
        {"**", "<strong>"},
        {"*", "<em>"},
        {"[]", "<a href=\""},
        {"]", "\"><a>"},
        {"####", "<h4>"},
        {"#####", "<h5>"},
        {"######", "<h6>"},
        {"1. ", "<li>"},
        {"- ", "<li>"},
        {"\n", "<br/>"}
    };

    // Regular expressions for matching Markdown elements
    static const std::regex bold_regex("\\*\\*(.*?)\\*\\*");
    static const std::regex italic_regex("\\*(.*?)\\*");
    static const std::regex link_regex("\\[(.*?)\\]\\((.*?)\\)");
    static const std::regex header_regex("^(#+)( .*?)$");
    static const std::regex list_regex("^(\\d+|\\-|\\*)\\s+(.*?)$");
    static const std::regex inline_code_regex("`(.+?)`");

    std::string html = std::string(markdown);

    // Replace bold text
    std::sregex_iterator it(markdown.begin(), markdown.end(), bold_regex);
    std::sregex_iterator end;
    for (const std::smatch& match : std::make_subrange(it, end)) {
        html.replace(match.position(0), match.length(0), markdown_to_html["**"] + match.str(1) + markdown_to_html["**"]);
    }

    // Replace italic text
