    }

    // Replace links
    std::sregex_iterator it3(markdown.begin(), markdown.end(), link_regex);
    std::sregex_iterator end3;
    for (const std::smatch& match : std::make_subrange(it3, end3)) {
        html.replace(match.position(0), match.length(0), markdown_to_html["[]"] + match.str(2) + match.str(1) + markdown_to_html["]"]);
    }

    // Replace headers
    std::sregex_iterator it4(markdown.begin(), markdown.end(), header_regex);
    std::sregex_iterator end4;
    for (const std::smatch& match : std::make_subrange(it4, end4)) {
        std::string header_level = match.str(1);
        std::string header_text = match.str(2);
        html.replace(match.position(0), match.length(0), "<" + header_level.length() + "h>" + header_text + "</" + header_level + "h>");
    }

    // Replace lists
    std::sregex_iterator it5(markdown.begin(), markdown.end(), list_regex);
    std::sregex_iterator end5;
    for (const std::smatch& match : std::make_subrange(it5, end5)) {
        std::string list_type = match.str(1);
        std::string list_item = match.str(2);
        if (list_type == "1.") {
