```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map>
#include <algorithm>

int main() {
    std::string word;
    std::string fileName;
    std::unordered_map<std::string, int> wordCount;

    // Prompt the user for the word to search for and the file name
    std::cout << "Enter the word to search for: ";
    std::cin >> word;
    std::cout << "Enter the file name: ";
    std::cin >> fileName;

    // Open the file
    std::ifstream file(fileName);
    if (!file.is_open()) {
        std::cerr << "Error: File could not be opened." << std::endl;
        return 1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Convert the line to lowercase to make the comparison case-insensitive
        std::transform(line.begin(), line.end(), line.begin(), ::tolower);

        // Split the line into words
        std::istringstream wordStream(line);
        std::string wordFromLine;
        while (wordStream >> wordFromLine) {
            // Check if the current word is the target word
            if (wordFromLine == word) {
                // Increment the count for the word
                wordCount[wordFromLine]++;
            }
        }
    }

    // Close the file
    file.close();

    // Print the count of the target word
    if (wordCount.find(word) != wordCount.end()) {
        std::cout << "The word '" << word << "' appears " << wordCount[word] << " times in the file." << std::endl;
    } else {
        std::cout << "The word '" << word << "' does not appear in the file." << std::endl;
    }

    return 0;
}
