#include <iostream>
#include <fstream>
#include <sstream>
#include <string>

int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return -1; // Return -1 to indicate an error
    }

    int count = 0;
    std::string line;

    // Read the file line by line
    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string token;

        // Split the line into words
        while (iss >> token) {
            // Compare each word (case-insensitive)
            if (token == word) {
                ++count;
            }
        }
    }

    file.close();
    return count;
}

int main() {
    std::string filename;
    std::string word;

    // Get user input for the filename and the word to search for
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> word;

    int occurrences = countWordOccurrences(filename, word);
    if (occurrences != -1) {
        std::cout << "The word '" << word << "' occurs " << occurrences << " times in the file." << std::endl;
    }

    return 0;
}
