int countWordInLine(const std::string &line, const std::string &word) {
    std::string lineCopy = line;
    std::transform(lineCopy.begin(), lineCopy.end(), lineCopy.begin(), ::tolower);
    std::string wordCopy = word;
    std::transform(wordCopy.begin(), wordCopy.end(), wordCopy.begin(), ::tolower);

