    if (!interaction.isCommand()) return;

    if (interaction.commandName === 'ban') {
        if (!interaction.memberPermissions.has('BAN_MEMBERS')) {
            return interaction.reply({ content: 'You do not have permission to use this command.', ephemeral: true });
        }

        const user = interaction.options.getUser('user');
        const reason = interaction.options.getString('reason');

        if (!user) {
            return interaction.reply({ content: 'You must specify a user to ban.', ephemeral: true });
        }

        try {
            await interaction.guild.members.ban(user, { reason: reason || 'No reason provided.' });
            interaction.reply({ content: `User ${user.tag} has been banned.`, ephemeral: true });
        } catch (error) {
            interaction.reply({ content: 'There was an error trying to ban that user.', ephemeral: true });
        }
    }
});

client.login('your-token-goes-here');
```

