#include <string>
#include <regex>

std::string markdownToHtml(std::string& markdownText) {
    // Replace **bold** text with <b>bold</b>
    std::regex boldRegex("\\*{2}([^*]+)\\*{2}");
    markdownText = std::regex_replace(markdownText, boldRegex, "<b>$1</b>");

    // Replace *italic* text with <i>italic</i>
    std::regex italicRegex("\\*([^\\*]+)\\*");
    markdownText = std::regex_replace(markdownText, italicRegex, "<i>$1</i>");

    // Wrap the whole text in <p> tags
    markdownText = "<p>" + markdownText + "</p>";

    return markdownText;
}
```

