const { Client, Collection, GatewayIntentBits } = require('discord.js');
const { REST } = require('@discordjs/rest');
const { Routes } = require('discord-api-types/v10');

// Create a new client instance
const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMembers] });

// Create a new collection to store commands
client.commands = new Collection();

// Add the /ban command to the collection
client.commands.set('ban', {
  data: new SlashCommandBuilder()
    .setName('ban')
    .setDescription('Ban a user from the server')
    .addUserOption(option =>
      option.setName('user')
        .setDescription('The user to ban')
        .setRequired(true))
    .addStringOption(option =>
      option.setName('reason')
        .setDescription('The reason for the ban')),
  async execute(interaction) {
    // Check if the user has the necessary permissions
    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      return interaction.reply({ content: 'You do not have permission to ban members.', ephemeral: true });
    }

    // Get the user to ban and the reason for the ban
    const user = interaction.options.getUser('user');
    const reason = interaction.options.getString('reason') || 'No reason provided';

    // Ban the user
    try {
      await interaction.guild.members.ban(user, { reason });
      interaction.reply({ content: `User ${user.tag} has been banned.`, ephemeral: true });
    } catch (error) {
      interaction.reply({ content: `Failed to ban user ${user.tag}.`, ephemeral: true });
    }
  },
});

