const { Client, Rest } = require('discord.js');
const { token } = require('./config.json');
const clientId = 'YOUR_CLIENT_ID'; // Replace with your bot's Client ID
const guildId = 'YOUR_TARGET_GUILD_ID'; // Replace with the ID of the server where the bot will operate
const commandPrefix = '!'; // You can change the command prefix if you want

const rest = new Rest({ version: '10' }).setToken(process.env.TOKEN);
const client = new Client({ intents: [] });

client.on('ready', () => {
    console.log(`Logged in as ${client.user.tag}!`);
});

// Register the command
