#include <string>
#include <regex>

std::string markdownToHTML(const std::string& markdown) {
    std::string html;

    // Convert headings
    std::regex headingRegex("#+\\s*(.*)");
    std::string headingReplacement("<h$1>$2</h$1>");
    html = std::regex_replace(markdown, headingRegex, headingReplacement);

    // Convert italics
    std::regex italicRegex("_([^_]+)_");
    std::string italicReplacement("<em>$1</em>");
    html = std::regex_replace(html, italicRegex, italicReplacement);

    // Convert bold
    std::regex boldRegex("\\*([^\\*]+)\\*");
    std::string boldReplacement("<strong>$1</strong>");
    html = std::regex_replace(html, boldRegex, boldReplacement);

    // Convert links
    std::regex linkRegex("\\[([^\\]]+)\\]\\(([^\\)]+)\\)");
    std::string linkReplacement("<a href=\"$2\">$1</a>");
    html = std::regex_replace(html, linkRegex, linkReplacement);

    return html;
}

int main() {
    std::string markdownText = "# Header\nThis is an _italic_ text. This is a **bold** text. Visit [example.com](http://example.com).";
    std::string htmlText = markdownToHTML(markdownText);
    std::cout << htmlText << std::endl;
    return 0;
}
