#include <string>
#include <pandocfilters.h>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    try {
        // Create a Pandoc filter to convert Markdown to HTML
        pcrecpp::RegEx re("#.*$"); // Remove headers
        auto filter = Pandocfilters::createFilter(
            Pandocfilters::Filter::Reader,
            Pandocfilters::Filter::Writer,
            Pandocfilters::Filter::H,
            Pandocfilters::Filter::Inlines,
            Pandocfilters::Filter::RawBlock,
            Pandocfilters::Filter::Str,
            [re](const std::string& name, const std::vector<std::string>& args, void* data) -> bool {
                if (name == "Header") {
                    if (re.hasMatch(args[0])) {
                        return false; // Remove headers
                    }
                }
                return true;
            },
            nullptr
        );

        // Convert Markdown to HTML using Pandoc filters
        filter->processMarkdown(markdown, &html);

        // Print the HTML string
        std::cout << "HTML: " << html << std::endl;
    } catch (const std::exception& e) {
        std::cerr << "Error converting Markdown to HTML: " << e.what() << std::endl;
        return "";
    }

    return html;
}

int main() {
    std::string markdown = "Hello, **world!** This is a sample **markdown** text.";
    std::string html = markdownToHtml(markdown);
    if (html.empty()) {
        std::cout << "Failed to convert Markdown." << std::endl;
    } else {
        std::cout << "HTML: " << html << std::endl;
    }
    return 0;
}
```

