        while (getline(lineStream, word, ' ')) { // Split by spaces
            if (word == targetWord) { // Ignore case sensitivity (optional)
                word = boost::to_lower_copy(word); // Use Boost's to_lower_copy if needed
                if (word == boost::to_lower_copy(targetWord))
                    occurrences++; // Increment count if words match
            }
        }
    }
    file.close();
    return occurrences;
}

int main() {
    string fileName, targetWord;

    cout << "Enter file name: ";
    getline(cin, fileName);
    cout << "Enter target word to count: ";
    getline(cin, targetWord);

