#include <stdio.h>

// Function to find common elements
int findCommon(int arr1[], int arr2[], int m, int n) {
    int i, j;
    printf("Common elements are: ");
    for (i = 0, j = 0; i < m && j < n; ) {
        if (arr1[i] == arr2[j]) {
            printf("%d ", arr1[i]); // Print common element
            i++; j++; // Move both pointers
        } else if (arr1[i] < arr2[j])
            i++; // Move pointer for arr1 if not equal
        else
            j++; // Move pointer for arr2 if not equal
    }
    printf("\n");
    return 0;
}

int main() {
    int arr1[] = {1, 2, 3, 4, 5};
    int arr2[] = {4, 5, 2, 6, 7};
    int m = sizeof(arr1) / sizeof(arr1[0]);
    int n = sizeof(arr2) / sizeof(arr2[0]);

    findCommon(arr1, arr2, m, n);

    return 0;
}
