addCommand('docker', () => {
  // Start Docker container
  cy.task('docker:start');
});

addCommand('docker:stop', () => {
  // Stop Docker container
  cy.task('docker:stop');
});

// Add screenshot capture
addCommand('screenshot', (options = {}) => {
  // Capture screenshot
  cy.screenshot(options);
});

addCommand('screenshot:save', (options = {}) => {
  // Save screenshot to file
  cy.screenshot(options).then((image) => {
    const filename = `${options.name ||'screenshot'}.png`;
    cy.writeFile(`cypress/screenshots/${filename}`, image, 'base64');
  });
