\usepackage{tikz}
\usetikzlibrary{shapes.geometric}

\newcommand{\chesssquare}[1]{%
  \node[draw, rectangle, minimum size=1.5cm, inner sep=0pt] (#1) {};
}

\begin{document}

\begin{tikzpicture}
  % Define the chessboard dimensions (8x8)
  \def\boardsize{8}
  \def\squaresize{1.5cm}

  % Draw the chessboard
  \foreach \x in {1,...,\boardsize} {
    \foreach \y in {1,...,\boardsize} {
      \ifodd\y
        \chesssquare{square-\x-\y}
      \else
        \chesssquare{square-\x-\y}
      \fi
    }
  }

  % Add some styling to the squares
  \foreach \x in {1,...,\boardsize} {
    \foreach \y in {1,...,\boardsize} {
      \ifodd\y
        \node[fill=gray!20] at (\x-\boardsize/2,\y-\boardsize/2) {};
      \fi
    }
  }

  % Add some styling to the squares (alternative shading)
  \foreach \x in {1,...,\boardsize} {
    \foreach \y [count=\yi] in {1,...,\boardsize} {
      \ifodd\yi
        \node[fill=gray!20] at (\x-\boardsize/2,\y-\boardsize/2) {};
      \fi
    }
  }
\end{tikzpicture}

\end{document}
```
