```c++
#include <string>

namespace MarkdownConverter {

    template <typename MarkdownParser>
    static std::string toHtml(const std::string& markdown, MarkdownParser& parser) {
        std::string htmlContent = parser.parse(markdown);
        return fmt::format(R"(<!DOCTYPE html><html><body>{}</body></html>)", htmlContent); // HTML skeleton
    }

    // Factory function to adapt to different Markdown parser libraries
    template <typename ParserType>
    static MarkdownParser createParserInstance() {
        //  Implementation specific to the chosen library (e.g., markdown-cpp, Sandcastle, etc.)
        if constexpr (std::is_same_v<ParserType, markdown_cpp::MarkdownConverter>) { 
            return markdown_cpp::MarkdownConverter{};  
        } else if (std::is_same_v<ParserType, YourSandcastleParserType>) { 
            return YourSandcastleParserInstance(); // Logic for Sandcastle 
        } else {
            //  Throw exception or provide a default if no matching parser type found
            throw std::runtime_error("Unsupported Markdown parser type"); 
        }
    }

    /**
     * Public, high-level function to convert Markdown to HTML.
     * It handles factory creation and conversion logic.
     */
    std::string convertMarkdown(const std::string& markdown, 
                             const std::string& parserLibraryName = "markdown-cpp") { // Optional library name for logging/config

        MarkdownParser parser = createParserInstance<ParserTypeForLibrary(parserLibraryName)>(markdown);
        return toHtml(markdown, parser); 
    }

    // Helper function to determine the ParserType based on the library name
    template <typename... Parsers>
    static auto ParserTypeForLibrary(const std::string& libraryName) {
        using ParserMapping = std::conditional_t<
            (std::is_same_v<libraryName, "markdown-cpp">, markdown_cpp::MarkdownConverter,
            std::is_same_v<libraryName, "sandcastle", YourSandcastleParserType, 
            //... Add more mappings for other libraries as needed...
            std::decay_t<std::remove_reference_t<Parsers>>); // Default or error case

        return ParserMapping; 
    } 

} // namespace MarkdownConverter

