     ```c
     void renderEntity(SDL_Renderer* renderer, Entity* entity, const Level* level) {
         SDL_Rect srcRect, destRect; // Source and destination rectangles

         //... (Calculate srcRect based on entity type, current state (e.g., snake direction) and tile size from sprite sheet)
         destRect.x = entity->position.x * TILE_SIZE; // Map entity position to screen coordinates
         destRect.y = entity->position.y * TILE_SIZE;
         destRect.w = TILE_SIZE; // Or sprite sheet tile size, if different
         destRect.h = TILE_SIZE; 

         SDL_RenderCopy(renderer, entity->spriteSheet, &srcRect, &destRect); 
     }

     void renderLevel(SDL_Renderer* renderer, const Level* level) {
         for (int y = 0; y < level->mapHeight; y++) {
             for (int x = 0; x < level->mapWidth; x++) {
                 int tileIndex = level->levelData[x + y * level->mapWidth]; // Get tile based on grid position
                 //... (Calculate srcRect from tileIndex on the LevelSheet and draw it) 
                 SDL_RenderCopy(renderer, level->levelSheet, &srcRect, &destRect); // Draw tile
