                           font=\small\sffamily, inner sep=0pt, outer sep=0pt}, % Default piece style
        alternatingSquare/.style={draw, fill=ifthenelse(\value{col} mod 2 = 0, white, gray!20)}
    }

    \begin{document}
        \begin{tikzpicture}[scale=0.8, every node/.style={chesspiece, overlay}] % Overlay for dynamic piece placement
            \coordinate (A1) at (0,0); % Anchor for coordinate system
            \def\setupBoard{
                \foreach \x in {1,..., \boardSize} {
                    \foreach \y in {a,..., \ifnum\boardSize=8 {h} \else {7} \fi} {
                        \node[alternatingSquare] (square-\y\x) at (\x*1.5, -\y*1.5) {};
                        \coordinate[anchor=center] (P\y\x) at (square-\y\x.center); 
