                }
            }

            \processFEN
            \setupBoard % Generate board structure first

            \end{tikzpicture}
    \end{document}   
    % Here's the FEN processing logic within a separate macro for readability

    \def\processFEN {
        \let\tempFEN = \fenInput 
        \xparseInput{\tempFEN} % Use xparse to efficiently parse FEN

        \def\parseFENLine#1{% Recursive helper to break down FEN lines
            \ifx\empty{#1} \return \else
                \xparse{
                    \strsplit{#1}{ / } into \parts
                    \foreach \part in {\parts} {
                        \xparse{ \strsplit{\part}{ } into \fields } % Fields within a rank
                        \ifnum\length(\fields)=1 \setPiecePos{\fields} \else \setPieceData{\fields} \fi
                    } 
                    \processFEN \parseFENLine{\nextLine} 
                }           
            \fi 
