def longest_common_subsequence(text1, text2):
    """
    Finds the longest common subsequence of two strings using dynamic programming.

    Args:
        text1 (str): The first input string.
        text2 (str): The second input string.

    Returns:
        tuple: A tuple containing the LCS string and its length.
    """

    n, m = len(text1), len(text2)
    # Utilize list comprehension for concise DP table initialization
    dp = [[0 for _ in range(m + 1)] for _ in range(n + 1)] 

    # Build the DP table in bottom-up fashion
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if text1[i - 1] == text2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])  

    # Extract LCS string using backtracking
    i, j = n, m
    lcs_string = ""
    while i > 0 and j > 0:
        if text1[i - 1] == text2[j - 1]:
            lcs_string = text1[i - 1] + lcs_string  
            i -= 1
            j -= 1
        else:
            if dp[i - 1][j] > dp[i][j - 1]:
                i -= 1
            else:
                j -= 1

    return lcs_string, dp[n][m]  # Return string and its length

