    }

    // Now,'merged' holds potentially common elements *in sorted order* up to index k.
    // Here's the in-place writing back to arr1 (optimize for space)

    int commonIndex = 0;  
    for (int l = 0; l < k; l++) {
        if (merged[l] == arr1[commonIndex]) { // Check if already present in a growing 'common' section of arr1
            arr1[commonIndex] = merged[l];  // Overwrite if needed (to keep common elements contiguous)
            commonIndex++;  
        } else {  // If new common element, shift existing elements (optimization)
            //  Use a'shift' approach to maintain contiguous common elements in arr1:
            for (int m = commonIndex; m > 0 && merged[l] < arr1[m - 1]; m--) {
                arr1[m] = arr1[m - 1];  
            }
            arr1[0] = merged[l]; // Place new common element at the front 
            commonIndex++;
        }
    }

    // Finally, truncate arr1 to the size of the found common elements.
    arr1.resize(commonIndex);  

    // Output the common elements (now in the modified arr1)
    for (int x : arr1) {
        std::cout << x << " "; 
    }
}

int main() {
    std::vector<int> arr1 = {2, 5, 1, 3, 8};
    std::vector<int> arr2 = {1, 3, 6, 8, 9};
    findCommonElementsInPlace(arr1, arr2);  // Output: 1 3 8 
    return 0;
}
