public class PowerOfThree {
    public boolean isPowerOfThree(int n) {
        // Check if n is less than or equal to zero, as it cannot be a power of three
        if (n <= 0) {
            return false;
        }

        // Calculate the base-3 logarithm of n
        double log = Math.log(n) / Math.log(3);

        // Check if the result is an integer (i.e., n is a power of three)
        return Double.valueOf(log).intValue() == log;
