```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>
#include <map>

int main(int argc, char* argv[]) {
    // Check if the user has provided a filename
    if (argc < 2) {
        std::cerr << "Usage: " << argv[0] << " <filename>" << std::endl;
        return 1;
    }

    // Open the file for reading
    std::ifstream file(argv[1]);
    if (!file.is_open()) {
        std::cerr << "Could not open file: " << argv[1] << std::endl;
        return 1;
    }

    // Get the word to search for from the command line arguments
    std::string wordToFind = argc > 2 ? argv[2] : "the";

    // Create a map to store the word count
    std::map<std::string, int> wordCount;

    // Read the file line by line and count the occurrences of the word
    std::string line;
    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string word;
        while (iss >> word) {
            // Convert to lowercase for case-insensitive comparison
            std::transform(word.begin(), word.end(), word.begin(), ::tolower);
            if (word == wordToFind) {
                // Increment the word count for the found word
                ++wordCount[word];
            }
        }
    }

    // Close the file
    file.close();

    // Output the results
    std::cout << "The word \"" << wordToFind << "\" occurs " << wordCount[wordToFind] << " times in the file \"" << argv[1] << "\"." << std::endl;

    return 0;
}
```

