```javascript
const { Client, Collection, Perms, SlashCommandBuilder } = require('discord.js@14');
const { token } = require('./config.json');

const client = new Client({ intents: ['Guilds', 'GuildMembers', 'GuildBans', 'GuildModeration'] });

client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  const { commandName, options, member, guild } = interaction;

  if (commandName === 'ban') {
    const userToBan = options.getUser('user');
    const reason = options.getString('reason') || 'No reason provided';

    if (userToBan.id === member.user.id) {
      return interaction.reply({ content: 'You cannot ban yourself, Captain!', ephemeral: true });
    }

    if (!member.permissions.has('BAN_MEMBERS')) {
      return interaction.reply({ content: 'You do not have permission to use this command.', ephemeral: true });
    }

    const memberToBan = await guild.bans.fetch(userToBan.id);
    if (memberToBan) {
      return interaction.reply({ content: `User ${userToBan.tag} is already banned.`, ephemeral: true });
    }

    try {
      await guild.members.ban(userToBan, { reason: reason });
      interaction.reply({ content: `User ${userToBan.tag} has been banned successfully. Reason: ${reason}`, ephemeral: true });
    } catch (error) {
      console.error(error);
      interaction.reply({ content: 'There was an error banning the user.', ephemeral: true });
    }
  }
});

client.login(token);
