std::string markdownToHtml(std::string_view markdownText) {
    md4c_document* doc = md4c_parse(nullptr, markdownText.data());
    if (!doc) {
        throw std::runtime_error("Failed to parse Markdown");
    }

    std::string htmlText;
    md4c_document_to_html(doc, &htmlText);

    md4c_free_document(doc);
    return htmlText;
}

