        std::string htmlText = markdownToHtml(markdownText.c_view());
        return htmlText;
    } catch (const std::exception& e) {
        std::cerr << "Error converting Markdown to HTML: " << e.what() << std::endl;
        // Optionally, rethrow the exception or handle it as needed
