public class PowerOfThree {

    public boolean isPowerOfThree(int n) {
        if (n <= 0) return false;  // Handling negative numbers directly

        return Math.log(n) / Math.log(3) 
            == Math.round(logValue(n) / ln3)  // ln3 is natural logarithm base 3
            && n > 0 && n <= (int) Math.pow(3, Integer.highestOneBit(n) - 1);

        // Or, using BigDecimal for exact representation of real division:
        BigDecimal base3 = new BigDecimal(String.format("%.0f", Math.log(n) / Math.log(3)));
        return base3.compareTo(BigDecimal.valueOf(Integer.toString(n).length() * Math.log(3))) == 0
               and
           BigDecimal.valueOf(n).compareTo(BigDecimal.valueOf(3).pow(Integer.highestOneBit(Integer.abs(n)) - 1)) >= 0;
    }

    private double logValue(int n) {
        return Math.log(n) / Math.log(3);
    }

    // Main function test
    public static void main(String[] args) {
        System.out.println(new PowerOfThree().isPowerOfThree(27));  // prints: true
        System.out.println(new PowerOfThree().isPowerOfThree(0));   // prints: false
        System.out.println(new PowerOfThree().isPowerOfThree(-1));  // prints: false
    }
