    }
};

bool initialized = false;

void _ready() {
    if (!initialized) {
        // Initialize game here
        initialized = true;
    }
}

int main() {
    // Create a window with a scene
    Godot::init("res://" MAIN_WINDOW_TITLE, false);
    Godot::set_keep_screen_on(true);
    Godot::set_low_processor_usage_mode(false);

