#include <iostream>
#include <string>
#include <regex>

std::string markdownToHTML(std::string const& markdown) {
    // Replace the markdown format with the corresponding HTML tags
    std::string html;

    std::regex rx_heading("([#]+)([\\w\\s]+)");
    std::regex rx_bold("([*]+)([\\w\\s]+)");
    std::regex rx_italic("([_]+)([\\w\\s]+)");
    std::regex rx_ul("([-]+)([\\w\\s]+)");
    std::regex rx_ol("([\\d]+\\.)([\\w\\s]+)");
    std::regex rx_code("(`+)([^`]+)");
    std::regex rx_inline_code("(`+)([^`]*)");

    bool match_heading = std::regex_search(markdown, rx_heading);
    bool match_bold = std::regex_search(markdown, rx_bold);
    bool match_italic = std::regex_search(markdown, rx_italic);
    bool match_ul = std::regex_search(markdown, rx_ul);
    bool match_ol = std::regex_search(markdown, rx_ol);
    bool match_code = std::regex_search(markdown, rx_code);
    bool match_inline_code = std::regex_search(markdown, rx_inline_code);

    if (match_heading) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_heading);

        std::string header_tag = "<h" + std::to_string(sm[1].length()) + ">";
        html += header_tag + sm[2] + "</h" + std::to_string(sm[1].length()) + ">";
    }

    if (match_bold) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_bold);

        html += "<strong>" + sm[2] + "</strong>";
    }

    if (match_italic) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_italic);

        html += "<em>" + sm[2] + "</em>";
    }

    if (match_ul) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_ul);

        html += "<ul>\n";
        html += markdownToHTML(sm[2]);
        html += "</ul>\n";
    }

    if (match_ol) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_ol);

        html += "<ol>\n";
        html += "<li>";
        html += markdownToHTML(sm[2]);
        html += "</li>\n";
        html += "</ol>\n";
    }

    if (match_code) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_code);

        html += "<code>" + sm[2] + "</code>";
    }

    if (match_inline_code) {
        std::smatch sm;
        std::regex_match(markdown, sm, rx_inline_code);

        html += "<code>" + sm[2] + "</code>";
    }

    return html;
}

int main() {
    std::string markdown_text = "*Hello, world!*";
    std::cout << "Original Markdown:\n" << markdown_text << "\n\nConverted HTML:\n";
    std::string html_text = markdownToHTML(markdown_text);
    std::cout << html_text;

    return 0;
}
```

