def longest_common_subsequence(s1, s2): 
    """This function finds the longest common subsequence of two strings s1, s2 
       using dynamic programming.
    """
    n1, n2 = len(s1), len(s2)
    matrix = [[None for x in range(n2+1)] for x in range(n1+1)]
    for i in range(n1): 
        for j in range(n2): 
            if (s1[i] == s2[j]): 
                matrix[i+1][j+1] = matrix[i][j] + 1
            else: 
                matrix[i+1][j+1] = max(matrix[i][j-1], matrix[i-1][j]) 
    return matrix[n1][n2]

def findNthFibonacci(n): 
    if n == 0: 
        return 0
    elif n == 1: 
        return 1
    else: 
        return findNthFibonacci(n-1) + findNthFibonacci(n-2) 

