  // Check first number in array
  let isPrimeNumber = arr[0] % 1 == 0;

  // If any number is not a valid prime number, return false
  if (!isPrimeNumber) {
    return false;
  }

  // Check remaining numbers
  for (let i = 1; i < arr.length; i++) {
    isPrimeNumber = arr[i] % 1 == 0;

    // If any number is not a valid prime number, return false
    if (!isPrimeNumber) {
      return false;
    }
  }

  // All numbers are valid prime numbers, return true
