def insert_sorted(arr, x): 
    low = 0 
    high = len(arr)-1

    while (low <= high): 
        mid = (low + high) // 2
        if arr[mid] == x: 
            return 
        elif arr[mid] < x: 
            low = mid + 1
        else: 
            high = mid - 1

    arr[low] = x 
    return low 

def search_sorted(arr, x): 
    low = 0
    high = len(arr)-1

    while (low <= high): 
        mid = (low + high) // 2

        if arr[mid] == x: 
            return mid
        else: 
            low = mid + 1

